chrome.runtime.onMessage.addListener((async(o,e,t)=>{let n=[];if(o.coupangs.length>0){const e=o.coupangs;n=n.concat(e.map((o=>({url:o})))),console.log("쿠팡 이미지 URL 목록:",n)}if(o.images.length>0){const e=o.images;n=n.concat(e.map((o=>({url:o})))),console.log("Gmarket 이미지 URL 목록:",n)}if(console.log("백그라운드 스크립트 이미지 URL 목록:",n),n.length>0)try{await fetch("http://localhost:8000/pic_to_text",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({imageUrls:n})}).then((o=>console.log("OCR STATUS : ",o.statusText)))}catch(o){console.error("POST 요청 전송 중 오류 발생:",o)}else console.log("가져올 리스트가 없습니다.")}));