let srcList=[],coupangList=[];const getAllCoupangImages=()=>{const e=document?.querySelectorAll("iframe");e.length>0&&e.forEach((e=>{try{const t=e.contentDocument;if(t){const e=t.querySelectorAll("img");srcList.push(...Array.from(e).map((e=>e.src)))}}catch(e){console.warn("Error accessing iframe contents:",e)}})),coupangList=[];const t=document.querySelectorAll(".product-detail-content-inside");t.length>0&&t?.forEach((e=>{try{const t=e.querySelectorAll("img");coupangList.push(...Array.from(t).map((e=>e.src)))}catch(e){console.warn("Error accessing iframe contents:",e)}}))};chrome.runtime.onMessage.addListener(((e,t,c)=>{"performTask"===e.action&&setTimeout((()=>{getAllCoupangImages(),chrome.runtime.sendMessage({images:srcList,coupangs:coupangList},(e=>{}))}),2e3)}));