let srcList=[],coupangList=[],combinedText="";const getAllCoupangImages=()=>{const e=document?.querySelectorAll("iframe");e.length>0&&e.forEach((e=>{try{const t=e.contentDocument;if(t){const e=t.querySelectorAll("img");srcList.push(...Array.from(e).map((e=>e.src)))}}catch(e){console.warn("Error accessing iframe contents:",e)}})),coupangList=[];const t=document.querySelectorAll(".product-detail-content-inside");t.length>0&&t?.forEach((e=>{try{const t=e.querySelectorAll("img");coupangList.push(...Array.from(t).map((e=>e.src)))}catch(e){console.warn("Error accessing iframe contents:",e)}}))},currentURL=window.location.href;console.log("현재 페이지 URL:",currentURL);const prodBuyElement=document.querySelector(".prod-buy");if(prodBuyElement){const e=prodBuyElement.querySelector("h2").textContent.trim(),t=prodBuyElement.querySelector(".total-price"),o=prodBuyElement.querySelector(".prod-shipping-fee-message"),r=prodBuyElement.querySelector(".prod-reward-cash").querySelectorAll("p"),n=Array.from(r).map((e=>e.textContent.trim())).join("\n"),c=t.textContent.trim(),l=o.textContent.trim();console.log("h2Text:",e),console.log("totalPriceText:",c),console.log("shippingFeeText:",l),console.log("combinedRewardsText:",n),combinedText=[e,c,l,n].join("\n"),console.log("combinedText:",combinedText)}setTimeout((()=>{getAllCoupangImages(),chrome.runtime.sendMessage({images:srcList,coupangs:coupangList,currentURL,detailTexts:combinedText},(e=>{}))}),2e3);